import React from 'react';
import ReactDOM from 'react-dom';
import { Form, Input, Button, InputNumber, Select } from 'antd';

import './index.css';
import 'antd/dist/antd.css';

class Chain extends React.Component {

  // state = { channels: "A, B, C", organizations: [{name: "x", peers: 100}, {name: "y", peers: 200}] };
   state = { channels: "", organizations: [], opvalue: "", text: "", orgwrite: 0, peerwrite: 0};

  onChannelsChange = e => {
    this.setState({channels: e.target.value});
  };
  onTextChange = e => {
    this.setState({text: e.target.value});
  };

  onAddOrganization = () => {
    const arr = [...this.state.organizations];
    arr.push({id: arr.length.toString(), name: '', peers: 0});
    this.setState({organizations: arr});
  };

  onChangeorganizationName = (e, o) => {
    const arr = [...this.state.organizations];
    console.log('name: ' + e.target.value);
    console.log('id: ' + o.id); // +id
    arr[o.id]={id: o.id, name:e.target.value, peers: o.peers}
    this.setState({organizations: arr});

    // TODO: change the current array (organizations) with the new name
  };

  addPeers = (e, o) => {
    const arr = [...this.state.organizations];
    arr[o.id]={id: o.id, name:o.name, peers: e}
    this.setState({organizations: arr});
  };

 addorgwrite = value => {
  this.setState({orgwrite: value});
  console.log('changed', value);
  };
 addpeerwrite = value => {
  this.setState({peerwrite: value});
  console.log('changed', value);
  };






  gotoaskserver = () => {
   fetch('http://localhost:8080/api/create', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         organizations :this.state.organizations,
         channels :this.state.channels,
    })
  })
  .then(function (data) {  
    console.log('Request success: ', data);  
  })  
  .catch(function (error) {  
    console.log('Request failure: ', error);  
  });

  };

  gotobuild = () => {
   fetch('http://localhost:8080/api/build', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         organizations :this.state.organizations,
         channels :this.state.channels,
    })
  })
  .then(function (response) {  
        return response.json();

  })  
  .then(function(json) {
    alert(json.msg);
    console.log(json);
  });

  };

  gotowrite = () => {
   fetch('http://localhost:8080/api/write', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         text :this.state.text,
         channels :this.state.channels,
         orgwrite: this.state.orgwrite, 
         peerwrite: this.state.peerwrite,
    })
  })
  .then(function (data) {  
    console.log('Request success: ', data);  
  })  
  .catch(function (error) {  
    console.log('Request failure: ', error);  
  });

  };


  gotoread = () => {
   fetch('http://localhost:8080/api/read', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         channels :this.state.channels,
         orgwrite: this.state.orgwrite, 
         peerwrite: this.state.peerwrite,
    })
  })
  .then(function (response) {  
        return response.json();

  })  
  .then(function(json) {



    alert(json.msg);
    console.log(json);
  });

  };


  gotodown = () => {
   fetch('http://localhost:8080/api/down', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         text :this.state.text,
         channels :this.state.channels,
    })
  })
  .then(function (response) {  
        return response.json();

  })  
  .then(function(json) {
    alert(json.msg);
    console.log(json);
  });

  };

  onChange1(value) {
  console.log(`selected ${value}`)
  };

  onBlur() {
  console.log('blur')
  };

  onFocus() {
  console.log('focus')
  };

  onSearch(val) {
  console.log('search:', val)
  };


  render() {
    const Option = Select;
    return (
        <div>
        <div>
          <div>WELCOME</div>
          <div>blockchain pro</div>
          <div>PRESS + TO ADD ORG</div>
          <Button type="primary" shape="circle" icon="plus" onClick={this.onAddOrganization} />
          <div>Organizations</div>
          {this.state.organizations.map(o => {
            return <div key={o.id}>
              <Input placeholder='Organization Name' value={o.name} onChange={(e) => this.onChangeorganizationName(e, o)}/>
              <InputNumber value={o.peers} onChange={(e) => this.addPeers(e, o)} />

            </div>
          })}

         <Button type="primary" shape="circle" icon="play-circle" onClick={this.gotoaskserver} />

        </div>

	<div>

	</div>

        <div>
         <Button type="primary" onClick={this.gotobuild} >Build the nerwork</Button>
        </div>

          <Form.Item>
          <div>write file line </div>

          <Input placeholder='write file line'
            value={this.state.text}
            onChange={this.onTextChange}/>
          <div>      peers organization         </div>
            <InputNumber min={0} max={5} defaultValue={0}  onChange={ this.addpeerwrite} />
            <InputNumber min={1} max={5} defaultValue={0}  onChange={ this.addorgwrite} />

         <Button type="primary"  onClick={this.gotowrite} >submit write</Button>
        </Form.Item>
        <div>

        </div>

      <div> press here to read the chain   </div>
          <div>      peers organization         </div>
            <InputNumber min={0} max={5} defaultValue={0}  onChange={ this.addpeerwrite} />
            <InputNumber min={1} max={5} defaultValue={0}  onChange={ this.addorgwrite} />

         <Button type="primary" onClick={this.gotoread} >read chain</Button>

        <div>

        </div>


        <div>
         <Button type="primary" onClick={this.gotodown} >down the network</Button>
        </div>

   </div>

    );
  }
}

// ========================================

ReactDOM.render(
  <Chain />,
  document.getElementById('root')
);
