import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import { Form, Input, Button, InputNumber, Select, Spin, Icon, load,  Alert } from 'antd';
import Popout from 'react-popout'
import NewWindow from 'react-new-window'
import FetchLoader from 'fetch-loader-react';
import LoadingSpinner from 'react-loader-spinner'
import BounceLoader from 'react-spinners/BounceLoader'
import Img from 'react-image'



import './index.css';
import 'antd/dist/antd.css';

class Chain extends React.Component {

  // state = { channels: "A, B, C", organizations: [{name: "x", peers: 100}, {name: "y", peers: 200}] };
   state = { channel: [], organizations: [], opvalue: "", text: "", orgwrite: 0, peerwrite: 0, orgState: [],  loading: false, data:""};

  onChannelsChange = e => {
    this.setState({channels: e.target.value});
  };
  onTextChange = e => {
    this.setState({text: e.target.value});
  };

  onAddOrganization = () => {
    const arr = [...this.state.organizations];
    arr.push({id: arr.length.toString(), name: 'org'+(arr.length+1), peers: 0});
    this.setState({organizations: arr});

  };

  onAddChannel = () => {
    const arr = [...this.state.channel];
    arr.push({id: arr.length.toString(), name: 'channel'+(arr.length+1), peers: 0});
    this.setState({channel: arr});

  };

  onRmovOrganization = () => {
    const arr = [...this.state.organizations];
    arr.pop();
    this.setState({organizations: arr});
  };

  createSelectItems() {
     const Option = Select;
     var arr = [...this.state.organizations];
     var per=0;
     let items = []; 
     var k=0;
     var z;
     console.log('length: ' +arr.length); // +id
     for (var i = 0; i < arr.length; i++) {
          if(arr.length>0){
          console.log('arri: ' + arr[i].peers); // +id
          for (var j=0;j<arr[i].peers; j++){
                z= i+1;       
                k= z*10+j; 
                console.log('k: ' + k); // +id
                items.push(<Select  key={k} value={k}>{'peer'+j+'.'+'org'+z}</Select >); 
         }  
	}
          //here I will be creating my options dynamically based on
          //what props are currently passed to the parent component
     }
     return items;
  };  

  onDropdownSelected = value => {
     console.log(`selected ${value}`);
     var x = value%10;
     var y = value/10;
     this.setState({orgwrite: y});
     this.setState({peerwrite: x});
     console.log(`x ${x}`);
     console.log(`y ${y}`);
    //here you will see the current selected value of the select input
  };

  onChangeorganizationName = (e, o) => {
    const arr = [...this.state.organizations];
    console.log('name: ' + e.target.value);
    console.log('id: ' + o.id); // +id
    arr[o.id]={id: o.id, name:e.target.value, peers: o.peers}
    this.setState({organizations: arr});

    // TODO: change the current array (organizations) with the new name
  };

  addPeers = (e, o) => {
    const arr = [...this.state.organizations];
    arr[o.id]={id: o.id, name:o.name, peers: e}
    this.setState({organizations: arr});
  };

 addorgwrite = value => {
  this.setState({orgwrite: value});
  console.log('changed', value);
  };
 addpeerwrite = value => {
  this.setState({peerwrite: value});
  console.log('changed', value);
  };

  gotoaskserver = async () => {
   await fetch('http://localhost:8080/api/create', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         organizations :this.state.organizations,
         channels :this.state.channels,
    })
  })
  .then(function (data) {  
    console.log('Request success: ', data);  
  })  
  .catch(function (error) {  
    console.log('Request failure: ', error);  
  });

  };

  gotobuild =  () => {
   this.setState({ loading: true, buildsuccess: false, builddata:'' }, () => {
     fetch('http://localhost:8080/api/build', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         organizations :this.state.organizations,
         channels :this.state.channels,
    })
  })
  .then(function (response) {  
        return response.json();

  })  
  .then(function(json) {
    // alert(json.msg);
     return (json);

    // load: false,
     //data: (json.msg),

   // console.log(json);
  })   .then(json => this.setState({
        loading: false,
	buildsuccess: true,
        builddata: json.msg,
      }));
  });

  };

  gotowrite = () => {
  this.setState({ writeloading: true, writesuccess: false, writedata: '' }, () => {
   fetch('http://localhost:8080/api/write', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         text :this.state.text,
         channels :this.state.channels,
         orgwrite: this.state.orgwrite, 
         peerwrite: this.state.peerwrite,
    })
  })
  .then(function (response) {  
        return response.json();

  })  
  .then(function(json) {
     //alert(json.msg);
     return (json);

    // load: false,
     //data: (json.msg),

   // console.log(json);
  })   .then(result  => this.setState({
        writeloading: false,
        writesuccess: true,
        writedata: result.msg,
      }));
  });

  };

  gotoread = () => {
     this.setState({ readloading: true }, () => {
     fetch('http://localhost:8080/api/read', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         channels :this.state.channels,
         orgwrite: this.state.orgwrite, 
         peerwrite: this.state.peerwrite,
    })
  })
  .then(function (response) {  
        return  response.json();
  })  
  .then(function(json) {
       var i,line;
       line='';

       var myWindow = window.open('', '', "width=500, height=300");
       for(i=0;i<json.msg.length;i++){
          if(json.msg[i] != '\n'){
             line+=json.msg[i];
          }
          else{
                          console.log(line);
                   myWindow.document.write("<p>" + line + "</p>");
                   line='';
          }

       }
        //myWindow.blur();

      // alert(json.msg);
    //console.log(json);
  })   .then(json => this.setState({
        readloading: false,
      }));
  });

  };


  gotodown = () => {
   fetch('http://localhost:8080/api/down', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         text :this.state.text,
         channels :this.state.channels,
    })
  })
  .then(function (response) {  
        return response.json();

  })  
