import React from 'react';
import ReactDOM from 'react-dom';
import { Input, Button, InputNumber, Select } from 'antd';

import './index.css';
import 'antd/dist/antd.css';

class Chain extends React.Component {

  // state = { channels: "A, B, C", organizations: [{name: "x", peers: 100}, {name: "y", peers: 200}] };
   state = { channels: "", organizations: [], opvalue: "", text: "" };

  onChannelsChange = e => {
    this.setState({channels: e.target.value});
  };
  onTextChange = e => {
    this.setState({text: e.target.value});
  };

  onAddOrganization = () => {
    const arr = [...this.state.organizations];
    arr.push({id: arr.length.toString(), name: '', peers: 0});
    this.setState({organizations: arr});
  };

  onChangeorganizationName = (e, o) => {
    const arr = [...this.state.organizations];
    console.log('name: ' + e.target.value);
    console.log('id: ' + o.id); // +id
    arr[o.id]={id: o.id, name:e.target.value, peers: o.peers}
    this.setState({organizations: arr});

    // TODO: change the current array (organizations) with the new name
  };

  addPeers = (e, o) => {
    const arr = [...this.state.organizations];
    arr[o.id]={id: o.id, name:o.name, peers: o.peers+1}
    this.setState({organizations: arr});
  };

  gotoaskserver = () => {
   fetch('http://localhost:8080/api/create', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         organizations :this.state.organizations,
         channels :this.state.channels,
    })
  })
  .then(function (data) {  
    console.log('Request success: ', data);  
  })  
  .catch(function (error) {  
    console.log('Request failure: ', error);  
  });

  };

  gotobuild = () => {
   fetch('http://localhost:8080/api/build', {  
      method: 'POST',  
      headers: {
        'Content-Type': 'application/json',
       },        
        body: JSON.stringify({
         organizations :this.state.organizations,
         channels :this.state.channels,
    })
  })
  .then(function (data) {  
    console.log('Request success: ', data);  
  })  
  .catch(function (error) {  
    console.log('Request failure: ', error);  
  });

  };

  onChange1(value) {
  console.log(`selected ${value}`)
  };

  onBlur() {
  console.log('blur')
  };

  onFocus() {
  console.log('focus')
  };

  onSearch(val) {
  console.log('search:', val)
  };


  render() {
    const Option = Select;
    return (
        <div>
        <div>
          <div>Channels</div>
          <Input placeholder='Add some Channels: "A, B, C"'
            value={this.state.channels}
            onChange={this.onChannelsChange}/>

          <Button type="primary" shape="circle" icon="plus" onClick={this.onAddOrganization} />
          <div>Organizations</div>
          {this.state.organizations.map(o => {
            return <div key={o.id}>
              <Input placeholder='Organization Name' value={o.name} onChange={(e) => this.onChangeorganizationName(e, o)}/>
              <InputNumber value={o.peers} onChange={(e) => this.addPeers(e, o)} />

            </div>
          })}

         <Button type="primary" shape="circle" icon="play-circle" onClick={this.gotoaskserver} />

        </div>

	<div>

	</div>

        <div>
         <Button type="primary" onClick={this.gotobuild} >Build the nerwork</Button>
        </div>


          <div>write file line </div>
          <Input placeholder='write file line'
            value={this.state.text}
            onChange={this.onTextChange}/>
        <div>
         <Button type="primary">submit write</Button>
        </div>

       <div>    </div>
       <div> press here to read the chain   </div>
        <div>
         <Button type="primary">read chain</Button>
        </div>

   </div>

    );
  }
}

// ========================================

ReactDOM.render(
  <Chain />,
  document.getElementById('root')
);
